import React from "react";
import "./App.css";
import { Container, Row } from "react-bootstrap";
import TweetForm from "./Components/TweetForm/TweetForm";
import TweetsList from "./Components/TweetsList/TweetsList";
import { getData, sendData } from "../src/TweetsAPI/TweetsAPI";
import LoadSpinner from "./Components/LoadSpinner/LoadSpinner";
import Navbar from "./Components/Navbar/Navbar";
import { BrowserRouter as Router } from "react-router-dom";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
      showSpinner: false,
    };
  }

  /**
   * A function that handles tweet sending.
   * The function makes use of the TweetsAPI for posting the tweets.
   * @param {Object} newTweet
   */
  handleOnAddTweet(newTweet) {
    this.setState({ showSpinner: true });
    sendData(newTweet, () =>
      getData((tweets) => {
        this.setState({ tweets });
      })
    );
    this.setState({ showSpinner: false });
  }

  componentDidMount() {
    this.setState({ showSpinner: true });
    getData((tweets) => {
      this.setState({ tweets });
    });
    this.setState({ showSpinner: false });
  }

  render() {
    return (

        <div className="d-inline w-75 justify-content-center">
          <Row>
            <Navbar />
          </Row>

          <Container fluid className="mt-5">
            <TweetForm
              handleOnAddTweet={(newTweet) => this.handleOnAddTweet(newTweet)}
            />
          </Container>
          {this.state.showSpinner && (
            <LoadSpinner className="d-flex justify-content-center mt-5" />
          )}
          <TweetsList tweets={this.state.tweets} />
        </div>

    );
  }
}
export default App;
