import React from "react";
import { Button, Container, Form, Row } from "react-bootstrap";
import "./TweetForm.css";

class TweetForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweetText: "",
      username: "TheReactiveMan",
    };
  }

  onHandleTweet(event) {
    event.preventDefault();

    const newTweet = {
      username: this.state.username,
      text: this.state.tweetText,
      dateCreated: new Date().toLocaleString(),
    };

    this.setState({ tweetText: "" });
    this.props.handleOnAddTweet(newTweet);
  }

  onHandleTweetTextChange(event) {
    this.setState({ tweetText: event.target.value });
  }

  render() {
    return (
      <Container fluid>
        <Row className="justify-content-center">
          <Form
            className="w-25"
            onSubmit={(event) => this.onHandleTweet(event)}
          >
            <Form.Group controlId="tweet-text-area">
              <Form.Control
                value={this.state.tweetText}
                className="tweet-form"
                as="textarea"
                rows={6}
                placeholder="What's on your mind..."
                onChange={(event) => this.onHandleTweetTextChange(event)}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Tweet
            </Button>
          </Form>
        </Row>
      </Container>
    );
  }
}
export default TweetForm;
