import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import { Container } from "react-bootstrap";
import TweetForm from "./Components/TweetForm/TweetForm";
import TweetsList from "./Components/TweetsList/TweetsList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }

  handleOnAddTweet(newTweet) {
    this.setState((state) => {
      //return { tweets: [newTweet, ...state.tweets] }; //  Add new nothe to list
      axios.post(
        "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/",
        { newTweet }
      );
    });
  }

  componentDidMount() {
    axios
      .get(
        "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/"
      )
      .then((res) => {
        const tweets = res.data;
        this.setState((tweets) => {
          return { tweets: tweets };
        });
      });
  }

  render() {
    return (
      <div className="d-inline w-75 justify-content-center">
        <Container fluid className="mt-5">
          <TweetForm handleOnAddTweet={(newTweet) => this.handleOnAddTweet} />
        </Container>
        <TweetsList tweets={this.state.tweets} />
      </div>
    );
  }
}
export default App;
