import React from "react";
import axios from "axios";
import "./App.css";
import { Container } from "react-bootstrap";
import TweetForm from "./Components/TweetForm/TweetForm";
import TweetsList from "./Components/TweetsList/TweetsList";
import { getData, sendData } from "../src/TweetsAPI/TweetsAPI";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }

  handleOnAddTweet(newTweet) {
    // this.setState(
    //   //return { tweets: [newTweet, ...state.tweets] }; //  Add new nothe to list
    //   axios.post(
    //     "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet",
    //     newTweet
    //   )
    // );
    sendData(newTweet, this.fetchData);
  }

  async fetchData() {
    const response = await getData((tweets) => {
      this.setState({ tweets });
    });
  }

  componentDidMount() {
    this.fetchData().then();
  }

  render() {
    return (
      <div className="d-inline w-75 justify-content-center">
        <Container fluid className="mt-5">
          <TweetForm
            handleOnAddTweet={(newTweet) => this.handleOnAddTweet(newTweet)}
          />
        </Container>
        <TweetsList tweets={this.state.tweets} />
      </div>
    );
  }
}
export default App;
