import React from "react";
import { Button, Container, Form, Row, Alert } from "react-bootstrap";
import "./TweetForm.css";

class TweetForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      content: "",
      userName: "@TheReactiveMan",
      date: "",
    };
  }

  /**
   * The function creates a new tweet object and sends it to App.js
   * @param {event} event 
   */
  onHandleTweet(event) {
    event.preventDefault();
    const newTweet = {
      content: this.state.content,
      userName: this.state.userName,
      date: new Date().toISOString(),
    };
    this.setState({ content: "" });
    this.props.handleOnAddTweet(newTweet);
  }


  onHandleTweetTextChange(event) {
    this.setState({ content: event.target.value });
  }

  render() {
    return (
      <Container fluid>
        <Row className="justify-content-center">
          <Form
            className="w-50"
            onSubmit={(event) => this.onHandleTweet(event)}
          >
            <Form.Group controlId="tweet-text-area">
              <Form.Control
                value={this.state.content}
                className="tweet-form"
                as="textarea"
                rows={6}
                placeholder="What's on your mind..."
                onChange={(event) => this.onHandleTweetTextChange(event)}
              />
            </Form.Group>
            <Button
              variant="primary"
              type="submit"
              disabled={
                this.state.content.length > 140 ||
                this.state.content.length === 0
              }
            >
              Tweet
            </Button>
            <Alert
              variant="danger"
              className="mt-3"
              show={this.state.content.length > 140}
            >
              Tweet cannot contain more than 140 characters
            </Alert>
          </Form>
        </Row>
      </Container>
    );
  }
}
export default TweetForm;
