import React from "react";
import "./App.css";
import { Container } from "react-bootstrap";
import TweetForm from "./Components/TweetForm/TweetForm";
import TweetsList from "./Components/TweetsList/TweetsList";
import { getData, sendData } from "../src/TweetsAPI/TweetsAPI";
import LoadSpinner from "./Components/LoadSpinner/LoadSpinner";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }

  handleOnAddTweet(newTweet) {
    sendData(newTweet, () =>
      getData((tweets) => {
        this.setState({ tweets });
      })
    );
  }

  componentDidMount() {
    getData((tweets) => {
      this.setState({ tweets });
    });
  }

  render() {
    return (
      <div className="d-inline w-75 justify-content-center">
        <Container fluid className="mt-5">
          <TweetForm
            handleOnAddTweet={(newTweet) => this.handleOnAddTweet(newTweet)}
          />
          <LoadSpinner show={true} />
        </Container>

        <TweetsList tweets={this.state.tweets} />
      </div>
    );
  }
}
export default App;
